import logo from './4me.png';
import Red from '../Images/RedPerson.png';
import Green from '../Images/GreenPerson.png';
import Blue from '../Images/BluePerson.png';
import Yellow from '../Images/YellowPerson.png';
import Orange from '../Images/OrangePerson.png';
import Purple from '../Images/PurplePerson.png';
import { useNavigate } from 'react-router-dom';
import react, {useEffect, useState} from 'react';
import useAuth from '../hooks/useAuth';

const GamePage = ({location}) => {
  let ImageList = []
  const { auth } = useAuth();
  const [userlist, setList] = useState();
  const [Height, setHeight] = useState('100hv');
  const navigate = useNavigate();
  const [imageList, setImageList] = useState();
  const [image, setImage] = useState();
  const [time, setTime] = useState(30);
  useEffect(() => {
    if(auth[1] == null){
      navigate('/');
    }
    if(location.pathname === '/GamePage'){
      
      const interval = setInterval(() => {

      }, 1000)
            setList(auth[2].map(name => <li className='namelist'>{name}</li>))
            console.log(auth[4].length)
            for(let i = 0; i < auth[4].length; i++){
              if(auth[4][i] === 'Red'){
                ImageList.push(Red)
              }
              if(auth[4][i] === 'Green'){
                ImageList.push(Green)
              }
              if(auth[4][i] === 'Blue'){
                ImageList.push(Blue)
              }
              if(auth[4][i] === 'Yellow'){
                ImageList.push(Yellow)
              }
              if(auth[4][i] === 'Orange'){
                ImageList.push(Orange)
              }
              if(auth[4][i] === 'Purple'){
                ImageList.push(Purple)
              }
            }
            
        setImage(ImageList.map(colour => (<img className='playerimg' key={colour} src= {colour} />)))
        return () => clearInterval (interval);
    } 
},[auth])

useEffect(()=>{
  const letter = {"0": "A", "1": "B", "2": "C", "3": "D",  "4": "E", "5": "F", "6": "G", "7": "Blue", "8": "Yellow",  "9": "Orange", "10": "Purple", "11": "Green", "12": "Blue", "13": "Yellow",  "14": "Orange", "15": "Purple", "16": "Green", "17": "Blue", "18": "Yellow",  "19": "Orange", "20": "Purple", "21": "Green", "22": "Blue", "23": "Yellow"}[Math.random(0,23)];

})
  return (location.pathname === '/GamePage')
  ? (
    <div className="App" style={{ height: Height}}>
      <h1 className="App-header">
        Acronym for me
      </h1>
      <div className= "usercode">Game Code: {auth[1]}</div>
      <div className= 'playercontainer'>
      <ul className="list">
        {userlist}
      </ul>
      <ul>
        {image}
      </ul>
      </div>
      

    </div>
  ): null;
}

export default GamePage;
