import logo from './4me.png';
import { useNavigate } from 'react-router-dom';
import react, {useState } from 'react';
import useAuth from '../hooks/useAuth';
let ws = new WebSocket("ws://localhost:9090")
let x = 1;

const GameWaitingPageJoin = ({location}, {ws}) => {
    let userlist;
    

    const { auth } = useAuth();
    const [Height, setHeight] = useState('100hv');
    const navigate = useNavigate();
    const checkPlayerCount = () => {
        const payLoad = {
            "method": "count",
            "gameId": auth[1]
        }
        ws.send(JSON.stringify(payLoad))
    };
    
    if(typeof(auth[2]) !== 'undefined' || location.pathname === '/GameWaitingPageJoin'){;
        checkPlayerCount();
        userlist = auth[2].map(name => <li>{name}</li>)
    }
    if(location.pathname === '/GameWaitingPageJoin'){
        const interval = setInterval(() => {
            const payLoad = {
                "method": "count",
                "gameId": auth[1]
            }
            ws.send(JSON.stringify(payLoad))
        }, 5000)
    }
    return (location.pathname === '/GameWaitingPageJoin')
    ? (
        <div className="App" style={{ height: Height}}>
        <h1 className="App-header">
            Acronym for me
        </h1>
        <div>{auth[1]}</div>
        <ul>{userlist}</ul>
        </div>
        
    ): null;
}

export default GameWaitingPageJoin;
