import logo from './4me.png';
import playericon from './Images/PlayerIcon';
import { useNavigate } from 'react-router-dom';
import react, {useEffect, useState} from 'react';
import useAut from '../hooks/useAuth';

const GamePage = ({location}) => {
  const { auth } = useAuth();
  const [userlist, setList] = useState();
  const [Height, setHeight] = useState('100hv');
  const navigate = useNavigate();
  useEffect(() => {
    if(location.pathname === '/GameWaitingPageJoin'){
        const interval = setInterval(() => {
            //checkPlayerCount();
            setList(auth[2].map(name => <li>{name}</li>))

        }, 1000)
        return () => clearInterval (interval);
    }
    if(auth[1] == null){
        navigate('/');
    }
    
},[auth])
  return (location.pathname === '/GamePage')
  ? (
    <div className="App" style={{ height: Height}}>
      <h1 className="App-header">
        Acronym for me
      </h1>
      <img src={playericon} className="App-logo" alt="logo" />
      <img src={logo} className="App-logo" alt="logo" />
      <div></div>
    </div>
  ): null;
}

export default GamePage;
