import logo from './4me.png';
import { useNavigate } from 'react-router-dom';
import react, {useState} from 'react';
import useAuth from '../hooks/useAuth';
let ws = new WebSocket("ws://localhost:9090")
let x = 1;

const GameWaitingPage = ({location}) => {
    let userlist;
    const [, updateState] = React.useState();
    const forceUpdate = React.useCallback(() => updateState({}), []);

    const { auth } = useAuth();
    const [Height, setHeight] = useState('100hv');
    const navigate = useNavigate();
    const checkPlayerCount = () => {
        const payLoad = {
            "method": "count",
            "gameId": auth[1]
        }
        ws.send(JSON.stringify(payLoad))
    };
    
    if(typeof(auth[2]) !== 'undefined' ){;
        checkPlayerCount();
        userlist = auth[2].map(name => <li>{name}</li>)
        if(x < auth[2].length){
            forceUpdate();
            x = auth[2].length;
        }
        
    }
    setTimeout(() => {
    }, 1000)
    return (location.pathname === '/GameWaitingPage')
    ? (
        <div className="App" style={{ height: Height}}>
        <h1 className="App-header">
            Acronym for me
        </h1>
        <div></div>
        <ul>{userlist}</ul>
        <button type="button" className="App-button" onClick={() => navigate('/FindaGame')} >Start the game</button>
        </div>
        
    ): null;
}

export default GameWaitingPage;
