import useAuth from '../hooks/useAuth';
const ws = new WebSocket("ws://localhost:9090")

const RandomLobby = ({user}) =>{
    const { auth } = useAuth();
      if(user.nameR != ''){
        const payLoad = {
          "method": "RandomServer",
          "clientId" : auth[0],
          "clientName" : user.nameR
        }
        ws.send(JSON.stringify(payLoad))
      }else{
        alert("Fill in user name!");
      }
  }

export default randomLobby
