import logo from './4me.png';
import { useNavigate } from 'react-router-dom';
import react, {useEffect, useState } from 'react';
import useAuth from '../hooks/useAuth';
let ws = new WebSocket("ws://localhost:9090")
let x = 1;

const GameWaitingPageJoin = ({location}) => {

    const { auth } = useAuth();
    const [userlist, setList] = useState();
    const [Height, setHeight] = useState('100hv');
    const navigate = useNavigate();
    const checkPlayerCount = () => {
        const payLoad = {
            "method": "count",
            "gameId": auth[1]
        }
        ws.send(JSON.stringify(payLoad))
    };

    useEffect(() => {
        if(location.pathname === '/GameWaitingPageJoin'){
            const interval = setInterval(() => {
                //checkPlayerCount();
                setList(auth[2].map(name => <li>{name}</li>))

            }, 1000)
            if((auth[2].length >= 3 && auth[3] === 'Public') || auth[2].length == 6){
                setTime(time - 1);
            }  
            if(auth[1] == null){
                navigate('/');
            }
            return () => clearInterval (interval);
        }
    },[auth])

    useEffect(() =>{
        if(location.pathname === '/GameWaitingPageCreator'){
            if((auth[2].length >= 3 && auth[3] === 'Public') || auth[2].length == 6){
                setTimeout(() => {
                    setTime(time - 1);
                }, 1000)
                if(time == 0){
                    StartGame();
                }
            }
        }
    },[time])

    return (location.pathname === '/GameWaitingPageJoin')
    ? (
        <div className="App" style={{ height: Height}}>
        <h1 className="App-header">
            Acronym for me
        </h1>
        <div>{auth[1]}</div>
        <ul>{userlist}</ul>
        {clock && <div>Game Starting in {time}</div>}
        </div>
        
    ): null;
}

export default GameWaitingPageJoin;
