import './App.css';
import React, { useState, useCallback } from 'react';
import { useNavigate, useLocation, Route, Link } from 'react-router-dom';
import HomePage from "./pages/HomePage";
import FindaGame from "./pages/FindaGame";
import GameWaitingPageJoin from "./pages/GameWaitingPageJoin";
import useAuth from './hooks/useAuth';
import GameWaitingPageCreator from './pages/GameWaitingPageCreator';
import GamePage from './pages/GamePage';
let clientId = null;
const ws = new WebSocket("ws://localhost:9090")


function App() {
  const {setAuth} = useAuth();
  ws.onmessage = message =>{
    const response = JSON.parse(message.data);
    if(response.method === "connect"){
      clientId = response.clientID;
      console.log("Client id set successfully " + clientId)
      setAuth([clientId]);
    }

    if(response.method === "create"){
      console.log("game successfully created with id " + response.game.id);
      const game = response.game;
      const serverlist = [];
      game.clients.forEach(c =>{
        
        serverlist.push(c.clientName);
      })
      setAuth([clientId, response.game.id, serverlist])
    }

    if(response.method === "RandomServer"){
      const game = response.game;
      const duty = response.duty
      const serverlist = [];
      if(duty === "Parent"){
        game.clients.forEach(c =>{
        
          serverlist.push(c.clientName);
        })
        
        navigate('/GameWaitingPageCreator')
      }else{
        game.clients.forEach(c =>{
          serverlist.push(c.clientName);
        })
        if(location.pathname === '/GameWaitingPageCreator'){
          navigate('/GameWaitingPageCreator')
        }else{
          if(game.state === "waiting"){
            navigate('/GameWaitingPageJoin')
          }else {
            navigate('/GamePage')
          }
          
        }
      }
      setAuth([clientId, response.game.id, serverlist])
    }

    if(response.method === "join"){
      const game = response.game;
      game.clients.forEach(c =>{

        console.log(c.colour);
      })
      const serverlist = [];
      game.clients.forEach(c =>{
        serverlist.push(c.clientName);
      })
      setAuth([clientId, response.game.id, serverlist])
      if(location.pathname === '/GameWaitingPageCreator'){
        navigate('/GameWaitingPageCreator')
      }else{
        navigate('/GameWaitingPageJoin')
      }

    }

    if(response.method === "count"){
      const game = response.game;
      const serverlist = [];
      game.clients.forEach(c =>{
        serverlist.push(c.clientName);
      })

      setAuth([clientId, game.id, serverlist]);
      
    }

    if(response.method === "start"){
      navigate('/GamePage')
      
    }
  }
  
  
  const navigate = useNavigate();
  const location = useLocation();
  return (
    <div>
      <HomePage location={location}/>
      <FindaGame location={location}/>
      <GameWaitingPageCreator location={location}/>
      <GameWaitingPageJoin location={location}/>
      <GamePage location={location}/>
    </div>
  );
}
function randomLobby(){
  const { auth } = useAuth();
    if(user.nameR != ''){
      const payLoad = {
        "method": "RandomServer",
        "clientId" : auth[0],
        "clientName" : user.nameR
      }
      ws.send(JSON.stringify(payLoad))
    }else{
      alert("Fill in user name!");
    }
}

export default App ; randomLobby;
