import useAuth from '../hooks/useAuth';
const ws = new WebSocket("ws://localhost:9090")

const useRandomLobby = (props) =>{
    //console.log(props)
    const { auth } = useAuth();
    console.log(auth[0])
      if(props.nameR != ''){
        const payLoad = {
          "method": "RandomServer",
          "clientId" : auth[0],
          "clientName" : props.nameR
        }
        ws.send(JSON.stringify(payLoad))
      }else{
        alert("Fill in user name!");
      }
      return;
  }

export default useRandomLobby;
