import logo from './4me.png';
import { useNavigate } from 'react-router-dom';
import react, {useEffect, useState } from 'react';
import useAuth from '../hooks/useAuth';
const ws = new WebSocket("ws://localhost:9090")


const GameWaitingPageCreator = ({location}) => {

    const { auth } = useAuth();
    const [Height, setHeight] = useState('100hv');
    const navigate = useNavigate();
    const [userlist, setList] = useState();
    const [time, setTime] = useState(30);
    const [clock, setClock] = useState(false);
    const checkPlayerCount = () => {
        const payLoad = {
            "method": "count",
            "gameId": auth[1]
        }
        ws.send(JSON.stringify(payLoad))
    };

    
    useEffect(() => {
        if(location.pathname === '/GameWaitingPageCreator'){
            if(auth[1] == null){
                navigate('/');
            }else{
                const interval = setInterval(() => {
                    //checkPlayerCount();
                    setList(auth[2].map(name => <li>{name}</li>))
                }, 1000)
                console.log(auth[3])
                if((auth[2].length >= 3 && auth[3] === 'Public') || auth[2].length == 6){
                    setTime(time - 1);
                    setClock(true);
                }  
                return () => clearInterval (interval);
            }
        }
        
    },[auth])
    useEffect(() =>{
        if(location.pathname === '/GameWaitingPageCreator'){
            if((auth[2].length >= 3 && auth[3] === 'Public') || auth[2].length == 6){
                setTimeout(() => {
                    setTime(time - 1);
                }, 1000)
                if(time == 0){
                    StartGame();
                }
            }
        }
    },[time])


    const StartGame = () =>{
        const payLoad = {
            "method": "start",
            "gameId" : auth[1]
        }
        ws.send(JSON.stringify(payLoad))
    }
    return (location.pathname === '/GameWaitingPageCreator')
    ? (
        <div className="App" style={{ height: Height}}>
        <h1 className="App-header">
            Acronym for me
        </h1>
        <div className= "usercode">Game Code: {auth[1]}</div>
        <ul >{userlist}</ul>
        <button type="button" className="App-button" onClick={() =>StartGame()} >Start the game</button>
        {clock && <div>Game Starting in {time}</div>}
        </div>
        
    ): null;
}

export default GameWaitingPageCreator;
