import logo from './4me.png';
import { useNavigate } from 'react-router-dom';
import react, {useEffect, useState } from 'react';
import useAuth from '../hooks/useAuth';
const ws = new WebSocket("ws://localhost:9090")


const GameWaitingPageCreator = ({location}) => {
 
    

    const { auth } = useAuth();
    let userlist = auth[2];
    const [Height, setHeight] = useState('100hv');
    const navigate = useNavigate();
    const checkPlayerCount = () => {
        console.log(auth[1]);
        //const payLoad = {
        //    "method": "count",
        //    "gameId": auth[1]
        //}
        //ws.send(JSON.stringify(payLoad))
    };

    
 
    if(location.pathname === '/GameWaitingPageCreator'){
        const interval = setInterval(() => {
            checkPlayerCount();
            userlist = auth[2].map(name => <li>{name}</li>)
        }, 5000)
    }

    
    return (location.pathname === '/GameWaitingPageCreator')
    ? (
        <div className="App" style={{ height: Height}}>
        <h1 className="App-header">
            Acronym for me
        </h1>
        <div>{auth[1]}</div>
        <ul>{userlist}</ul>
        <button type="button" className="App-button" onClick={() => navigate('/FindaGame')} >Start the game</button>
        </div>
        
    ): null;
}

export default GameWaitingPageCreator;
