//import logo from './4me.png';
import './FindaGame.css';
import { useNavigate } from 'react-router-dom';
import React, {useState} from 'react';
import useAuth from '../hooks/useAuth';
import RandomLobby from '../hooks/useRandomLobby';
const ws = new WebSocket("ws://localhost:9090")

const FindaGame = ({location}) => {
  const navigate = useNavigate();
  const [Height, setHeight] = useState('100hv');
  const [user, setUser] =useState({
    nameR: '',
    nameS: '',
    nameC: '',
    code: '',
  });
  const { auth } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const randomLobby = () => {
    RandomLobby(user)
    /*
    if(user.nameR != ''){
      const payLoad = {
        "method": "RandomServer",
        "clientId" : auth[0],
        "clientName" : user.nameR
      }
      ws.send(JSON.stringify(payLoad))
    }else{
      alert("Fill in user name!");
    }
      */
  };

  const CreateLobby = () => {
    if(user.nameC != ''){
      const payLoad = {
        "method": "create",
        "clientId" : auth[0],
        "clientName" : user.nameC
      }
      ws.send(JSON.stringify(payLoad))
      navigate('/GameWaitingPageCreator')
    }else{
      alert("Fill in user name!");
    }
  };

  const JoinSpecificLobby = () =>{
    if(user.nameS != '' || user.code !=''){
      const payLoad = {
        "method": "join",
        "clientId" : auth[0],
        "clientName": user.nameS,
        "gameId" : user.code
      }
      
      ws.send(JSON.stringify(payLoad))
      navigate('/GameWaitingPageJoin')
    }else{
      if(user.nameS == ''){
        alert("Fill in user name!")
      }else{
        alert("Fill in lobby code")
      }
    }
    

  }
  return (location.pathname === '/FindaGame')
  ? (
    <div className="App" style={{ height: Height}}>
      <h1 className="App-header">
        Acronym 4 Me
      </h1>
      <div className = "container">
      <h2 className = "findrandomgame"> 
        Search for a random lobby
        <div className = "space">
          <div>
            Name
          </div>
          <input
            className = "textbox"
            type="text"
            name="nameR"
            value={user.nameR}
            onChange={handleChange}
            />
            
        </div>
        <button className = "button-84" onClick={() => RandomLobby()}>
          Join Game
        </button>
      </h2>
      <h2 className = "entergamecode"> 
        Search for a lobby using a code
        <div className = "space">
          <div>
            Name
          </div>
          <input
            className = "textbox"
            type="text"
            name="nameS"
            value={user.nameS}
            onChange={handleChange}
            />
        </div>
        <div className = "space">
          <div>
            Code
          </div>
          <input
            className = "textbox"
            type="text"
            name="code"
            value={user.code}
            onChange={handleChange}
            />
        </div>
        <button className = "button-84" onClick={() => JoinSpecificLobby()}>
          Join Game
        </button>
      </h2>
      </div>
      <h3 className = "createagame">
        Create a Lobby
        <div className="space" >
          <div>
            Name
          </div>
          <input
            className = "textbox2"
            type="text"
            name="nameC"
            value={user.nameC}
            onChange={handleChange}
            />
        </div>
        <button className = "button-2" onClick={() => CreateLobby()}>
          Create Game
        </button>
      </h3>
    </div>
  ): null;
}

export default FindaGame;
